import React, { Fragment, useState } from "react";
import { useHistory } from "react-router-dom";

import QuoteItem from "./QuoteItem/QuoteItem";

import classes from "./QuoteList.module.css";

const QuoteList = (props) => {
  const { quotes } = props;
  const history = useHistory();

  const [sortBy, setSortBy] = useState("asc");

  const onSortQuotes = () => {
    console.log(history);
    // history.push(`/quotes?sortBy=${sortBy}`);
    // setSortBy((prevState) => (prevState === "asc" ? "des" : "asc"));
  };

  const quoteElements = quotes.map((quote) => (
    <QuoteItem
      key={quote.id}
      id={quote.id}
      author={quote.author}
      text={quote.text}
    />
  ));

  return (
    <Fragment>
      <div className={classes.sorting}>
        <button onClick={onSortQuotes}>
          Sort {sortBy === "asc" ? "Ascending" : "Descending "}
        </button>
      </div>
      <ul className={classes.list}>{quoteElements}</ul>
    </Fragment>
  );
};

export default QuoteList;
